import { ethers } from 'ethers';
import DopeNft from "../utils/dopeNft.json";

//Address of deployed contract
const CONTRACT_ADDRESS = "0xcE15BBc069b34815163cc217bA0c32a1802E36F9";
// console.log(CONTRACT_ADDRESS);


// Call to the contract to mint NFT 
export const triggerContractToMint = async () => {
    try {
        const { ethereum } = window;
        console.log('here', CONTRACT_ADDRESS);
        if (ethereum) {
            const provider = new ethers.providers.Web3Provider(ethereum);
            const signer = provider.getSigner();
            const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, DopeNft.abi, signer);

            console.log("Going to pop wallet now to pay gas...");
            let nftTranx = await connectedContract.constructDopeNft();

            console.log("Mining...please wait.")
            await nftTranx.wait();

            console.log(`Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTranx.hash}`);

          } else {
            console.log("Ethereum object doesn't exist!");
          }
            } catch (error) {
          console.log(error)
        }
    };

// Event listener to the capture event function from contract.   
export const mintEventListener = async () => {
  try {
    const { ethereum } = window;
    if (ethereum) {
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const connectedContract = new ethers.Contract(
        CONTRACT_ADDRESS,
        DopeNft.abi,
        signer
      );

      // Create connection to contract and emits (throws) it when from address and tokenId is passed in.
      connectedContract.on("newAlienNFT", (from, tokenId) => {
        //safe check we have the right data
        console.log(from, tokenId.toNumber());
        alert(
          `Hey there! We've minted your NFT and sent it to your wallet. It may be blank right now. It can take a max of 10 min to show up on OpenSea. Here's the link: https://testnets.opensea.io/assets/${CONTRACT_ADDRESS}/${tokenId.toNumber()}`
        );
      });
    } else {
      alert(`Oops something went wrong`);
    }
  } catch (error) {
    console.log(error);
  }
};
